import { useEffect, useState } from 'react'
import { Card, CardActions, CardContent, CardMedia, Typography, IconButton, Box, TextField,FormControl } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import SearchIcon from '@mui/icons-material/Search';
import axios from 'axios'
import './App.css'

export default function App() {
  const [data, setData] = useState([])
  const imgPlaceholder = "data:image/png;base64,"


  useEffect(() => {
    axios.get('http://localhost:3000/usuario/maria')
      .then(response => setData(response.data))
  }, [])

  return (
    <Box sx={{ minWidth: '100%' }}>
      <TextField
        sx={{ width: '100%' }} id="outlined-basic" label="Pesquise o nome de usuÃ¡rio" variant="outlined"
        slotProps={{
          input: {
            startAdornment: <IconButton><SearchIcon /></IconButton>,
          },
        }}
      />
      <Box sx={{ display: 'flex' }}>
        {
          data.map((item, index) => (
            <Card sx={{ maxWidth: 345, p: 1, m: 1 }} key={index}>
              <CardMedia
                component='img'
                height='140'
                image={item.imagem ? item.imagem : imgPlaceholder}
              />

              <CardContent>
                <Typography variant='body2'>
                  {item.conteudo}
                </Typography>
              </CardContent>
              <CardActions>
                <IconButton><DeleteIcon color='error' /></IconButton>
              </CardActions>
            </Card>
          ))
        }
      </Box>
    </Box>
  )
}